export interface Candle {
  time: number;
  open: number;
  high: number;
  low: number;
  close: number;
  volume: number;
}

import { config } from '../config';

export interface ReversalSignal {
  detected: boolean;
  reason?: string;
  strength?: number;
  side?: 'Long' | 'Short';
}

// Calculate RSI
function calculateRSI(candles: Candle[], period: number = 14): number {
  if (candles.length < period + 1) return 50;

  const changes = [];
  for (let i = candles.length - period - 1; i < candles.length - 1; i++) {
    changes.push(candles[i + 1].close - candles[i].close);
  }

  const gains = changes.filter(c => c > 0);
  const losses = changes.filter(c => c < 0).map(c => Math.abs(c));

  const avgGain = gains.length > 0 ? gains.reduce((a, b) => a + b, 0) / period : 0;
  const avgLoss = losses.length > 0 ? losses.reduce((a, b) => a + b, 0) / period : 0;

  if (avgLoss === 0) return 100;
  const rs = avgGain / avgLoss;
  return 100 - (100 / (1 + rs));
}

// Check if at local bottom or top
function isLocalExtreme(candles: Candle[], lookback: number = 5, type: 'bottom' | 'top'): boolean {
  if (candles.length < lookback) return false;

  const recentCandles = candles.slice(-lookback);
  const currentCandle = recentCandles[recentCandles.length - 1];

  if (type === 'bottom') {
    const lowestOfRecent = Math.min(...recentCandles.map(c => c.low));
    return currentCandle.low <= lowestOfRecent * 1.001; // Allow 0.1% tolerance
  } else {
    const highestOfRecent = Math.max(...recentCandles.map(c => c.high));
    return currentCandle.high >= highestOfRecent * 0.999; // Allow 0.1% tolerance
  }
}

// Check for bullish or bearish candle
function isBullishCandle(candle: Candle): boolean {
  return candle.close > candle.open;
}

function isBearishCandle(candle: Candle): boolean {
  return candle.close < candle.open;
}

// Check for volume spike
function hasVolumeSpike(candles: Candle[], multiplier: number = 1.5): boolean {
  if (candles.length < config.strategy.volumeLookback + 1) return false;

  const recent = candles.slice(-config.strategy.volumeLookback - 1, -1);
  const avgVolume = recent.reduce((sum, c) => sum + c.volume, 0) / recent.length;
  const currentVolume = candles[candles.length - 1].volume;

  return currentVolume >= avgVolume * multiplier;
}

// Check trend direction using EMA
function getTrendDirection(candles: Candle[], period: number = 20): 'up' | 'down' | 'sideways' {
  if (candles.length < period) return 'sideways';

  const recent = candles.slice(-period);
  const ema = recent.reduce((sum, c) => sum + c.close, 0) / period;
  const currentPrice = candles[candles.length - 1].close;
  const priceChange = (currentPrice - ema) / ema;

  if (priceChange > 0.001) return 'up';
  if (priceChange < -0.001) return 'down';
  return 'sideways';
}

// Check momentum confirmation
function hasMomentumConfirmation(candles: Candle[], side: 'Long' | 'Short'): boolean {
  if (candles.length < 3) return false;

  const last3 = candles.slice(-3);

  if (side === 'Long') {
    // For longs, we want to see price stabilizing or starting to rise
    const priceChange = (last3[2].close - last3[0].close) / last3[0].close;
    return priceChange > -0.002; // Not falling more than 0.2%
  } else {
    // For shorts, we want to see price stabilizing or starting to fall
    const priceChange = (last3[2].close - last3[0].close) / last3[0].close;
    return priceChange < 0.002; // Not rising more than 0.2%
  }
}

// MAIN REVERSAL DETECTION - Now supports both longs and shorts
export function detectReversal(candles: Candle[]): ReversalSignal {
  if (candles.length < 20) {
    return { detected: false, reason: 'Not enough data' };
  }

  const currentCandle = candles[candles.length - 1];
  const rsi = calculateRSI(candles);
  const prevRsi = calculateRSI(candles.slice(0, -1));
  const trend = getTrendDirection(candles);

  // LONG SIGNAL - RSI oversold and climbing (more extreme threshold)
  if (rsi <= 35 && rsi > prevRsi && isLocalExtreme(candles, 5, 'bottom')) {
    if (!isBullishCandle(currentCandle)) {
      return { detected: false, reason: 'Waiting for green candle for long' };
    }

    if (!hasVolumeSpike(candles, config.strategy.volumeMultiplier)) {
      return { detected: false, reason: 'No volume spike for long' };
    }

    if (!hasMomentumConfirmation(candles, 'Long')) {
      return { detected: false, reason: 'No momentum confirmation for long' };
    }

    const strength = Math.min(1, ((35 - rsi) / 10 + (rsi - prevRsi) / 5) / 2);

    return {
      detected: true,
      side: 'Long',
      reason: `Long reversal! RSI ${rsi.toFixed(1)} climbing from ${prevRsi.toFixed(1)}`,
      strength: Math.max(0, Math.min(1, strength))
    };
  }

  // SHORT SIGNAL - RSI overbought and falling (aligned with current bullish market)
  // In bullish market (RSI 59.4), be more selective with shorts
  const shortRsiThreshold = trend === 'up' ? 68 : 65;

  if (rsi >= shortRsiThreshold && rsi < prevRsi && isLocalExtreme(candles, 5, 'top')) {
    if (!isBearishCandle(currentCandle)) {
      return { detected: false, reason: 'Waiting for red candle for short' };
    }

    if (!hasVolumeSpike(candles, config.strategy.volumeMultiplier)) {
      return { detected: false, reason: 'No volume spike for short' };
    }

    if (!hasMomentumConfirmation(candles, 'Short')) {
      return { detected: false, reason: 'No momentum confirmation for short' };
    }

    const strength = Math.min(1, ((rsi - shortRsiThreshold) / 15 + (prevRsi - rsi) / 5) / 2);

    return {
      detected: true,
      side: 'Short',
      reason: `Short reversal! RSI ${rsi.toFixed(1)} falling from ${prevRsi.toFixed(1)}`,
      strength: Math.max(0, Math.min(1, strength))
    };
  }

  return { 
    detected: false, 
    reason: `No reversal signal (RSI: ${rsi.toFixed(1)}, Trend: ${trend})` 
  };
}

// NO RECOVERY MODE - Just tight stops and discipline
import { config } from "../config";

export interface Position {
  id: string;
  side: "Long" | "Short";
  entryPrice: number;
  entryTime: number;
  collateral: number;
  leverage: number;
  stopLoss: number;
  takeProfit: number;
  minProfitTarget: number;
  maxProfitTarget: number;
  status: "open" | "closed";
  exitPrice?: number;
  exitTime?: number;
  pnl?: number;
  reason?: string;
  trailingStop?: number;
}

export interface PositionUpdate {
  shouldClose: boolean;
  reason?: string;
  exitPrice?: number;
}

export function createPosition(
  side: "Long" | "Short",
  entryPrice: number,
  collateral: number
): Position {
  const leverage = config.futures.leverage;
  const stopPercent = config.strategy.initialStopPercent / 100;
  const stopLoss = side === "Long"
    ? entryPrice * (1 - stopPercent)
    : entryPrice * (1 + stopPercent);

  const targetPercent = config.strategy.targetProfitPercent / 100;
  const takeProfit = side === "Long"
    ? entryPrice * (1 + targetPercent)
    : entryPrice * (1 - targetPercent);

  return {
    id: `pos-${Date.now()}-${Math.random().toString(36).substr(2, 4)}`,
    side,
    entryPrice,
    entryTime: Date.now(),
    collateral,
    leverage,
    stopLoss,
    takeProfit,
    minProfitTarget: config.strategy.minProfitDollars,
    maxProfitTarget: config.strategy.maxProfitDollars,
    status: "open" as const,
  };
}

export function updatePosition(
  position: Position,
  currentPrice: number
): PositionUpdate {
  const now = Date.now();
  const timeElapsed = (now - position.entryTime) / 1000;
  const positionSize = position.collateral * position.leverage;

  let pnlPercent: number;
  if (position.side === "Long") {
    pnlPercent = ((currentPrice - position.entryPrice) / position.entryPrice) * 100;
  } else {
    pnlPercent = ((position.entryPrice - currentPrice) / position.entryPrice) * 100;
  }
  const pnl = (pnlPercent / 100) * positionSize;

  // 1. STOP LOSS - Get out NOW
  if (position.side === "Long" && currentPrice <= position.stopLoss) {
    return { shouldClose: true, reason: "stop-loss", exitPrice: currentPrice };
  }
  if (position.side === "Short" && currentPrice >= position.stopLoss) {
    return { shouldClose: true, reason: "stop-loss", exitPrice: currentPrice };
  }

  // 2. MAX PROFIT - Lock it in
  if (pnl >= position.maxProfitTarget) {
    return { shouldClose: true, reason: "max-profit", exitPrice: currentPrice };
  }

  // 3. TAKE PROFIT
  if (position.side === "Long" && currentPrice >= position.takeProfit) {
    return { shouldClose: true, reason: "take-profit", exitPrice: currentPrice };
  }
  if (position.side === "Short" && currentPrice <= position.takeProfit) {
    return { shouldClose: true, reason: "take-profit", exitPrice: currentPrice };
  }

  // 4. MIN PROFIT
  if (pnl >= position.minProfitTarget) {
    return { shouldClose: true, reason: "min-profit", exitPrice: currentPrice };
  }

  // 5. TRAILING STOP - Move stop to breakeven after 0.06% profit
  if (pnlPercent >= 0.06 && !position.trailingStop) {
    // Update trailing stop to breakeven (handled externally)
    position.trailingStop = position.entryPrice;
  }

  // 6. QUICK EXIT - Only if actually profitable (not -$5 loss!)
  if (timeElapsed >= config.strategy.quickExitSeconds && pnl >= 0.50) {
    return { shouldClose: true, reason: "quick-exit-profit", exitPrice: currentPrice };
  }

  // 7. EXTENDED TIMEOUT with small loss tolerance
  if (timeElapsed >= config.strategy.maxTradeSeconds) {
    return {
      shouldClose: true,
      reason: pnl >= 0 ? "timeout-profit" : "timeout-loss",
      exitPrice: currentPrice
    };
  }

  // 8. STALE TRADE - If no movement after 2 minutes, exit at breakeven or small profit
  if (timeElapsed >= 120 && Math.abs(pnlPercent) < 0.02) {
    return { shouldClose: true, reason: "stale-exit", exitPrice: currentPrice };
  }

  return { shouldClose: false };
}

export function closePosition(
  position: Position,
  exitPrice: number,
  reason: string
): Position & { exitPrice: number; exitTime: number; pnl: number; reason: string } {
  const positionSize = position.collateral * position.leverage;
  let pnlPercent: number;
  if (position.side === "Long") {
    pnlPercent = ((exitPrice - position.entryPrice) / position.entryPrice) * 100;
  } else {
    pnlPercent = ((position.entryPrice - exitPrice) / position.entryPrice) * 100;
  }
  const pnl = (pnlPercent / 100) * positionSize;

  return {
    ...position,
    exitPrice,
    exitTime: Date.now(),
    pnl,
    status: "closed" as const,
    reason,
  };
}

// Kallisti's Reversal Scalper - Buy The Dip Strategy
// NOW SUPPORTS BOTH LONGS AND SHORTS

import { config } from "./config";
import { log, error } from "./logger";
import { BinanceClient } from "./exchange/binance";
import { detectReversal, Candle } from "./strategy/reversal-strategy";
import {
  createPosition,
  updatePosition,
} from "./risk/recovery-manager";
import { Ledger } from "./ledger";

function normalizeCandles(rawKlines: any[]): Candle[] {
  return rawKlines.map((k: any) => ({
    time: k[0],
    open: parseFloat(k[1]),
    high: parseFloat(k[2]),
    low: parseFloat(k[3]),
    close: parseFloat(k[4]),
    volume: parseFloat(k[5]),
  }));
}

let lastSignalTime = 0;
let lastSignalPrice = 0;
let lastSignalSide: string | null = null;
const MIN_SIGNAL_INTERVAL = 45000; // 45 seconds between signals
const MIN_PRICE_CHANGE = 0.0005; // 0.05% price change required for new signal

async function main() {
  log("\n Kallisti's Reversal Scalper Running");

  const client = new BinanceClient(config.dataSource.baseUrl);
  const ledger = new Ledger();
  await ledger.load();

  // Check if new day
  const now = new Date();
  const lastReset = new Date(ledger.state.lastReset);
  if (now.getUTCDate() !== lastReset.getUTCDate()) {
    const stats = ledger.stats;
    log(`\n DAILY SUMMARY (${lastReset.toLocaleDateString()})`);
    log(`   Balance: $${ledger.state.balance.toFixed(2)}`);
    log(`   P&L: $${stats.dailyPnl} (${stats.dailyPnlPercent}%)`);
    log(`   Trades: ${stats.totalTrades} (${stats.wins}W/${stats.losses}L)`);
    log(`   Win Rate: ${stats.winRate}%`);
    ledger.resetDaily();
    await ledger.save();
  }

  try {
    // Fetch 1m candles
    const klines = await client.getKlines(
      config.symbol,
      config.candleInterval,
      config.candleLimit
    );
    const candles = normalizeCandles(klines.list);
    const currentPrice = candles[candles.length - 1].close;

    // Update open positions
    const openBefore = [...ledger.openPositions];
    for (const position of openBefore) {
      const update = updatePosition(position, currentPrice);

      if (update.shouldClose) {
        const closed = await ledger.closePosition(
          position.id,
          update.exitPrice!,
          update.reason!
        );

        const pnlEmoji = (closed.pnl || 0) >= 0 ? "" : "";
        const timeElapsed = ((closed.exitTime || 0) - closed.entryTime) / 1000;
        log(`${pnlEmoji} CLOSED (${timeElapsed.toFixed(0)}s): ${position.side} $${closed.pnl?.toFixed(2)} | ${closed.reason}`);
      }
    }

    // Display status
    const stats = ledger.stats;
    log(` $${ledger.state.balance.toFixed(2)} | Daily: $${stats.dailyPnl} (${stats.dailyPnlPercent}%) | ${stats.totalTrades} trades (${stats.winRate}% win)`);
    log(` Open: ${ledger.openPositions.length} | Hour: ${ledger.state.tradesThisHour}/${config.risk.maxTradesPerHour}`);

    // Check if can trade
    const canOpen = ledger.canOpenPosition();
    if (!canOpen.allowed) {
      log(` ${canOpen.reason}`);
      return;
    }

    // Prevent duplicate signals - check time AND price AND side
    const currentTime = Date.now();
    const priceChange = lastSignalPrice > 0 ? Math.abs(currentPrice - lastSignalPrice) / lastSignalPrice : 1;

    if (currentTime - lastSignalTime < MIN_SIGNAL_INTERVAL) {
      log(` Waiting for signal cooldown (${Math.round((MIN_SIGNAL_INTERVAL - (currentTime - lastSignalTime)) / 1000)}s)`);
      return;
    }

    // Look for reversal signal (BOTH LONGS AND SHORTS)
    const signal = detectReversal(candles);

    if (!signal.detected) {
      log(` ${signal.reason}`);
      return;
    }

    // Additional duplicate check - same side and similar price
    if (signal.side === lastSignalSide && priceChange < MIN_PRICE_CHANGE) {
      log(` Duplicate signal blocked (same side, price only ${(priceChange * 100).toFixed(3)}% different)`);
      return;
    }

    log(` REVERSAL DETECTED: ${signal.reason} (strength: ${(signal.strength! * 100).toFixed(0)}%)`);

    // Create position based on signal side
    const position = createPosition(
      signal.side!,  // Use signal side (Long or Short)
      currentPrice,
      config.risk.positionSizeDollars
    );

    await ledger.openPosition(position);
    lastSignalTime = currentTime;
    lastSignalPrice = currentPrice;
    lastSignalSide = signal.side!;

    const posSize = (position.collateral * position.leverage).toFixed(0);
    const sideEmoji = signal.side === 'Long' ? '' : '';
    log(`${sideEmoji} ${signal.side?.toUpperCase()} $${posSize} (${position.leverage}x) @ $${position.entryPrice.toFixed(2)} | Target: +0.15% ($${(parseFloat(posSize) * 0.0015).toFixed(2)})`);

  } catch (err) {
    error(`Error: ${err instanceof Error ? err.message : String(err)}`);
  }
}

main().catch((err) => {
  error(`Fatal: ${err instanceof Error ? err.message : String(err)}`);
  process.exit(1);
});
